mapper.select:
select ud.user_id
from users_detail as ud
right join user_roles as urs on ud.user_id = urs.user_id and urs.role_id = #{roleId}
where ud.email = #{email}

select ud.user_id
from users_detail as ud
right join user_roles as urs on ud.user_id = urs.user_id and urs.role_id = #{roleId}
left join users as u on u.user_id = ud.user_id
where ud.email = #{email}

mapper.update:
update 
  users_detail 
set
  email = #{email}
where
  user_id = #{userId}

update 
  user_detail
set
  email = #{newEmail}
where
  user_id != #{userId} 
  and email = #{oldEmail}
